// Entrada
const data = items[0].json;

const pecas_cliente = data.pecas_cliente || [];
const pecas_ms = data.pecas_ms || [];
const pecas_reparadas = data.pecas_reparadas || [];
const pecas_inseridas = data.pecas_inseridas || [];
const mao = data.mao_de_obra?.[0] || {};

// ---- PROCESSAR MÃO DE OBRA ----
let datas = [];
let totalEfetivo = 0;
let totalContingencial = 0;

for (let i = 1; i <= 50; i++) {  // suporta até 50 registros
  const dataKey = `data${i}`;
  const efKey = `hh_efetivo${i}`;
  const ctKey = `hh_contingencial${i}`;

  if (mao[dataKey]) {
    datas.push(mao[dataKey]);

    totalEfetivo += Number(mao[efKey] || 0);
    totalContingencial += Number(mao[ctKey] || 0);
  }
}

datas.sort((a,b) => new Date(a) - new Date(b));

const primeiraData = datas[0] || null;
const ultimaData = datas[datas.length - 1] || null;

// ---- RETORNAR SOMENTE DADOS DIRETOS ----
return [
  {
    json: {
      pecas_cliente,
      pecas_medical_spin: pecas_ms,
      pecas_reparadas,
      pecas_inseridas,
      mao_de_obra: {
        primeiraData,
        ultimaData,
        totalEfetivo,
        totalContingencial,
        totalGeral: totalEfetivo + totalContingencial
      }
    }
  }
];

// Função para converter string JSON em array
function parseArray(str) {
  try {
    const arr = typeof str === "string" ? JSON.parse(str) : str;
    return Array.isArray(arr) ? arr : [];
  } catch {
    return [];
  }
}

// Função para enumerar objetos e transformar tudo em 1 objeto dentro de um array
function enumerateObjects(array, fields) {
  const obj = {};

  array.forEach((entry, index) => {
    const i = index + 1;
    fields.forEach(f => {
      obj[`${f}${i}`] = entry[f] ?? "";
    });
  });

  return [obj];
}

// Acumuladores
let pecas_cliente_all = [];
let pecas_ms_all = [];
let pecas_reparadas_all = [];
let pecas_inseridas_all = [];
let mao_de_obra_all = [];

// Junta todos os itens recebidos do nó anterior
for (const item of items) {
  const data = item.json;

  pecas_cliente_all.push(...parseArray(data.pecas_cliente));
  pecas_ms_all.push(...parseArray(data.pecas_ms));
  pecas_reparadas_all.push(...parseArray(data.pecas_reparadas));
  pecas_inseridas_all.push(...parseArray(data.pecas_inseridas));
  mao_de_obra_all.push(...parseArray(data.mao_de_obra));
}

// Agora enumera TODOS os campos
const final = {
  pecas_cliente: enumerateObjects(pecas_cliente_all, ["nome", "modelo", "sn", "detalhes"]),
  pecas_ms: enumerateObjects(pecas_ms_all, ["nome", "modelo", "sn", "detalhes"]),
  pecas_reparadas: enumerateObjects(pecas_reparadas_all, ["nome", "modelo", "sn", "detalhes"]),
  pecas_inseridas: enumerateObjects(pecas_inseridas_all, ["nome", "modelo", "sn", "detalhes"]),
  mao_de_obra: enumerateObjects(mao_de_obra_all, ["data", "hh_efetivo", "hh_contingencial"])
};

// Retorna tudo em UM ÚNICO ITEM
return [
  {
    json: final
  }
];

const data = items.map(i => i.json);

// Extrai info fixa (uma vez só)
const placeholder = data.find(d => d.placeholder)?.placeholder || null;
const index = data.find(d => d.Index !== undefined)?.Index || null;
const documentId = data.find(d => d.documentId)?.documentId || null;

// Extrai driveId e fileid (únicos)
const driveId = data.find(d => d.driveId)?.driveId || null;
const fileid = data.find(d => d.fileid)?.fileid || null;

// Pega todos os blocos com id + name
const arquivos = data.filter(d => d.id && d.name);

// Remove duplicados por id
const ids = new Set();
const unicos = arquivos.filter(a => {
  if (ids.has(a.id)) return false;
  ids.add(a.id);
  return true;
});

// Cria novo array de items no formato pedido
return unicos.map(a => ({
  json: {
    placeholder,
    index,
    documentId,
    driveId,
    fileid,
    id: a.id,
    name: a.name
  }
}));

