// Texto vindo do InputOS
let texto = $('InputOS').first().json.message.conversation || "";

// "OS_" continua obrigatório
if (!texto.includes("OS_")) {
  return [{ json: { encontrado: false, motivo: "Texto não contém 'OS_'" } }];
}

// Extrair conteúdo entre aspas → CNPJ
let match = texto.match(/"([^"]+)"/);
let termoBusca = "";

// Se veio "OS_" entre aspas → usa conteúdo das aspas
if (match) {
  termoBusca = match[1];
} else {
  // Sem aspas → pegar tudo após OS_
  const pos = texto.indexOf("OS_") + 3;
  termoBusca = texto.slice(pos).trim();
}

// Se depois de OS_ não tiver nada → erro
if (!termoBusca) {
  return [{ json: { encontrado: false, motivo: "Nenhum termo após OS_" } }];
}

// Itens do Sheets
const linhas = items;

// Procura termo dentro do campo NOME
const linhaEncontrada = linhas.find(item => {
  const nome = item.json.NOME || "";
  return nome.includes("OS_") && nome.includes(termoBusca);
});

// Não encontrou → retornar falso
if (!linhaEncontrada) {
  return [{ json: { encontrado: false, motivo: "Nenhuma linha encontrada" } }];
}

// Retorna linha encontrada
return [
  {
    json: linhaEncontrada.json
  }
];

