// 1. Pegar o texto vindo do InputDB
const conversation = $('InputDB').first().json.message.conversation || "";

// 2. Extrair tudo após o primeiro "_"
if (!conversation.includes("_")) {
    return [{ message: "Nenhum '_' encontrado no parâmetro recebido." }];
}

const searchKey = conversation.split("_")[1]; // tudo após o primeiro "_"


// 3. Dados vindos do nó 'sheets'
const rows = $('OS consult1').all();

let found = null;

for (const row of rows) {
    const data = row.json;

    // Criar objeto somente com as colunas permitidas
    const searchableFields = { ...data };
    delete searchableFields["NOME"];
    delete searchableFields["LINK"];

    // verificar se algum dos valores contém o searchKey
    const values = Object.values(searchableFields);

    for (const value of values) {
        if (value && value.toString().includes(searchKey)) {
            found = data; // retorna a linha inteira original
            break;
        }
    }

    if (found) break;
}


// 4. Caso não encontre nada
if (!found) {
    return [{
        message: `Nenhuma correspondência encontrada para: ${searchKey}`
    }];
}


// 5. Retorna a primeira linha encontrada
return [ found ];

