const items = $input.all(); 
const dataFaceFormatted = [];

// Fun√ß√£o para traduzir nomes de m√©tricas
function traduzirAcao(actionType) {
  const mapa = {
    "link_click": "Cliques no link",
    "page_engagement": "Envolvimento com a p√°gina",
    "landing_page_view": "Visualiza√ß√µes da p√°gina de destino",
    "omni_landing_page_view": "Visualiza√ß√µes da p√°gina de destino (Omni)",
    "post_engagement": "Envolvimento com a publica√ß√£o",
    "post_interaction_gross": "Intera√ß√µes na publica√ß√£o",
    "post_reaction": "Rea√ß√µes na publica√ß√£o",
    "video_view": "Visualiza√ß√µes de v√≠deo",
    "lead": "Leads",
    "offsite_complete_registration_add_meta_leads": "Cadastro conclu√≠do (Meta Leads)",
    "offsite_content_view_add_meta_leads": "Visualiza√ß√£o de conte√∫do (Meta Leads)",
    "offsite_search_add_meta_leads": "Busca (Meta Leads)",
    "onsite_conversion.lead_grouped": "Convers√µes (Lead agrupado)",
    "onsite_conversion.messaging_conversation_started_7d": "Mensagens iniciadas",
    "purchase": "Compras"
  };
  return mapa[actionType] || actionType;
}

items.forEach(item => {
  const data = item.json;

  let mensagens = 0;
  let linkClickValue = 0;
  let compras = 0;
  let leads = 0;

  let sp = parseFloat(data.spend || 0);  
  const impressions = parseInt(data.impressions || 0);
  const reach = parseInt(data.reach || 0);

  // Objeto final de m√©tricas separadas
  const metricasSeparadas = {};

  if (data.actions) {
    data.actions.forEach(action => {
      const value = parseInt(action.value || 0);
      const nomeTraduzido = traduzirAcao(action.action_type);

      // Cada m√©trica vira uma coluna pr√≥pria
      metricasSeparadas[nomeTraduzido] = value;

      if (action.action_type === 'onsite_conversion.messaging_conversation_started_7d') {
        mensagens = value;
      }
      if (action.action_type === 'link_click') {
        linkClickValue = value;
      }
      if (action.action_type === 'purchase') {
        compras = value;
      }
      if (action.action_type === 'lead' || action.action_type === 'onsite_conversion.lead_grouped') {
        leads += value; // soma leads
      }
    });
  }

  // Calcula m√©tricas principais
  let custoPorMensagem = mensagens > 0 ? (sp / mensagens).toFixed(2).replace(".", ",") : "0,00";
  let cpc = linkClickValue > 0 ? (sp / linkClickValue).toFixed(2).replace(".", ",") : "0,00";
  let cpm = impressions > 0 ? (sp / (impressions / 1000)).toFixed(2).replace(".", ",") : "0,00";
  let frequencia = reach > 0 ? (impressions / reach).toFixed(2).replace(".", ",") : "0,00";
  let cpl = leads > 0 ? (sp / leads).toFixed(2).replace(".", ",") : "0,00";
  let roas = sp > 0 && compras > 0 ? (compras / sp).toFixed(2).replace(".", ",") : "0,00";

  dataFaceFormatted.push({
    Data: data.date_start,
    Campanha: data.campaign_name,
    Gasto: sp.toFixed(2).replace(".", ","),
    Impress√µes: impressions,
    Alcance: reach,
    Frequ√™ncia: frequencia,
    Cliques: linkClickValue,
    CPC: cpc,
    CPM: cpm,
    Mensagens: mensagens,
    "Custo por Mensagens": custoPorMensagem,
    Leads: leads,
    CPL: cpl,
    Compras: compras,
    ROAS: roas,
    ...metricasSeparadas // üî• cada m√©trica traduzida em campo separado
  });
});

return dataFaceFormatted.map(r => ({ json: r }));

