// n8n Code Node (JavaScript)

// Pega os campos do Facebook (field_data array)
const fields = $json.field_data || [];

// Mapeamento dos possíveis nomes para os campos padrão
const fieldMap = {
  name: ["first_name", "nome", "nome_completo"],
  phone: ["phone_number", "telefone", "whatsapp_"],
  email: ["email"],
  clinic_name: [
    "qual_o_nome_da_sua_clínica?",
    "nome_da_clínica_/_empresa"
  ],
  clinic_details: ["fale_mais_sobre_sua_clínica"], // separado agora ✅
  machine_brand: ["qual_a_marca_da_sua_máquina?", "marca_da_máquina_de_ressonância"],
  machine_model: ["qual_o_modelo_da_bobina?", "modelo_da_máquina_de_rm"],
  problem: ["qual_o_problema_apresentado?"],
  machine_status: ["status_da_máquina"],
  mri_model: ["qual_o_modelo_da_ressonância_magnética?"]
};

// Função que procura o valor de um campo no field_data
function getFieldValue(aliases) {
  for (const alias of aliases) {
    const field = fields.find(f => f.name === alias);
    if (field && field.values && field.values.length > 0) {
      return field.values[0]; // Pega o primeiro valor
    }
  }
  return null;
}

// Monta o objeto final padronizado
const standardized = {};
for (const [standardKey, aliases] of Object.entries(fieldMap)) {
  standardized[standardKey] = getFieldValue(aliases);
}

// Retorna o resultado padronizado
return [{ json: standardized }];

