// Pega todos os itens e junta em um só objeto
const merged = Object.assign({}, ...$input.all().map(i => i.json));

// Retorna apenas um item
return [{ json: merged }];

const id = Number($json.idEstagio);

const FUNIS = {
  // ===== INBOUND / OUTBOUND =====
  INBOUND: [17, 15, 51, 50, 16, 14, 60],
  OUTBOUND: [11, 12, 7, 39, 31, 6, 9, 8],

  // ===== OUTROS FUNIS =====
  REATIVACAO_INBOUND: [34, 44, 35, 36, 37, 38],

  RESSONANCIA_MAGNETICA: [1, 53, 3, 4, 5, 19],

  CONTRATO_MANUTENCAO: [65, 66, 67, 68, 69, 70],

  BOBINAS_EXAMES: [25, 26, 27, 28, 61, 29, 59, 64],

  TOMOGRAFO: [20, 21, 22, 23, 24],

  ULTRASSOM: [71, 72, 73, 74, 75, 76],

  FOLLOW_UP: [40, 41],

  CAMPANHAS_FUTURAS: [45, 46, 47, 48, 52],
};

let funilDetectado = null;

for (const [funil, estagios] of Object.entries(FUNIS)) {
  if (estagios.includes(id)) {
    funilDetectado = funil;
    break;
  }
}

return {
  ...$json,
  idEstagioNormalizado: id,
  funil: funilDetectado ?? 'DESCONHECIDO',
};

