// Pega o telefone do node Edit Fields1
let phone = $('Edit Fields1').item.json.phone || '';

// Remove tudo que não é número
phone = phone.replace(/\D/g, '');

// Adiciona 55 na frente se não começar com 55
if (!phone.startsWith('55')) {
    phone = '55' + phone;
}

// Retorna o item com o telefone atualizado
return {
    json: {
        phone: phone
    }
};

