  if (typeof jid !== 'string' || !jid) return null;

  // Pega somente a parte antes do @
  let base = jid.split('@')[0].trim();
  if (!base) return null;

  // Adiciona @s.whatsapp.net
  return `${base}@s.whatsapp.net`;
}

// Prioriza o primeiro v√°lido, depois o segundo
